<?phpnamespace app\console\controller;use think\Db;use think\Request;use think\Url;use app\console\model\SystemLog as ThisModel;class AgentStatement extends Base{    /**	 * [index description]列表	 * @return [type] [description]	 */	public function index()	{        $where = array();        if (Request::instance()->isPost()) {            $name = Request::instance()->param('name');            if ($name) {                $where = [                    'u.username|s.content' => ['like', '%' . $name . '%'],                ];                $this->assign("name", $name);            }        }        /*		$data = db('system_log')->alias('s')                ->join('user u','u.id = s.user_id')                ->field('u.username,u.mobile,s.id,s.money,s.type,s.content,s.param,s.create_time,s.is_read')                ->order('s.id', 'desc')                ->where('s.type', '2')                ->where($where)                ->paginate(20);        */        $date = date('Y-m-d');        $arr  = ['','共享天使','共享店主','城市合伙人','联合合伙人'];        for($i=1;$i<5;$i++) {            $data[$i]['id']   = $i;            $data[$i]['name'] = $arr[$i];            $data[$i]['day']  = ThisModel::order('id', 'desc')->whereTime('create_time', '>=', $date)->where(['type' => 2, 'param' => $i])->count();            $data[$i]['count'] = ThisModel::order('id', 'desc')->where(['type' => 2, 'param' => $i])->count();        }        return $this->fetch('index', [            'list'       => $data        ]);	}	/**     * [create description]添加方法     * @return [type] [description]     */	public function create()	{		if (Request::instance()->isPOST())		{			$data = Request::instance()->post();			$result = ThisModel::saveVerify($data);			if (true === $result) {                $this->success('新建成功', 'console/user_message/index');            } else {                $this->error($result);            }		}		$param = $this->appendarg();		return $this->fetch('create', $param);	}    /**     * [update description]更新方法     * @param  [type] $id [description]主键id     * @return [type]     [description]     */	public function update($id)	{		if (Request::instance()->isPOST())		{			$data = Request::instance()->post();			$result = ThisModel::saveVerify($data,$id);			if (true === $result) {                $this->success('更新成功', 'console/user_message/index');            } else {                $this->error($result);            }		}		$data = ThisModel::get($id);		$param = array_merge(['vo'=>$data], $this->appendarg());		return $this->fetch('create', $param);	}    /**     * 添加修改时候需要传递参数的话，用此方法，只写一遍     */	public function appendarg(){		return [		   //添加参数		];	}    /**     * [delete description]删除方法 多选和单选删除     * @return [type] [description]     */	public function delete(){		if (Request::instance()->isPOST())		{			$id = Request::instance()->post('id/a'); // (/a)方法 将收到的id转为数组			$delmodel = ThisModel::destroy($id);			if($delmodel){			    $this->success('删除成功', 'console/user_message/index');			}			else{				$this->error($delmodel->getError());			}	    }	    else{	    	$this->error('请求方式出错!');	    }	}    /**     * [renewfield description]列表更新字段     * @return [type] [description]     */	public function renewfield()    {        if (Request::instance()->isPOST()) {            $data = Request::instance()->post();            $par['id'] = $data['id'];            $par['is_read'] = 1;            $this_model = new ThisModel();            if ($this_model->update($par)) {                $this->success('更新成功', 'console/user_message/index');            } else {                $this->error($this_model->getError());            }        }    }}