<?phpnamespace app\home\controller;use app\home\model\Banner;use app\home\model\Goods;use app\home\model\Store;use think\Controller;use think\Db;use think\Request;use think\Url;class Faq extends Controller{    /*     * 客服中心     * */	public function index(){		$data['tel'] = Db::name("webconfig")->where("varname", 'web_hotline')->value("varvalue");		$data['faq'] = Db::name("faq")->field("id,title,picurl")->select();		$data['phone'] = Db::name('webconfig')->where('id',7)->field('varvalue')->value('varvalue');		return $this->fetch(			'index',[				'title'=>"客服中心",				'vo'=>$data,			]		);	}	public function info(){		$id= Request::instance()->param('id');		$content = Db::name('faq')->where('id',$id)->value('content');		$aaa = Db::name('faq')->where('id',$id)->value('title');		return $this->fetch('info',[			'content'=>$content,			'title'=>"详情",			'tttt'=>$aaa,		]);	}}