<?phpnamespace app\home\controller;use think\Controller;use think\Db;use think\Request;use think\Url;class Share extends Controller{    /* @title 分享注册app     * @param user_id 用户id     * @url home/share/index     * */    function index() {        // 万淘应用宝下载地址        // http://a.app.qq.com/o/simple.jsp?pkgname=com.lc.wantaoshebeiwang        $param = Request::instance()->param();        $user_icode = Db::name('user')->where('id',$param['user_id'])->value('icode');        return $this->fetch('index',[            'title'=>"分享注册",            'icode'=>$user_icode,        ]);//        echo  "分享链接";die;    }    //分享下载    function sss(){        return $this->fetch('sss');    }}