<?phpnamespace app\index\controller;use think\Db;use think\Request;use think\Url;use app\index\model\Kehu as ThisModel;class Kehu extends Base{	public $arr_type=[];    /**	 * [index description]列表	 * @return [type] [description]	 */	public function index(ThisModel $Kehu)	{        $where = array();        $where = ['status'=>1];        $res  = $Kehu->where($where)->order('id desc')->paginate(12,true);        $this->assign('page',$res->render());		$url = request()->domain().Request::instance()->url();		$this->assign('url',$url);		$this->assign('share_title', '宝泉岭尊业汽车 - 客户风采');		return $this->fetch('index', [            'data'       => $res        ]);	}    public function show($id){       $data =  ThisModel::get($id);	   $url = request()->domain().Request::instance()->url();		$this->assign('url',$url);		$this->assign('share_title', '宝泉岭尊业汽车 - 客户风采');       $this->assign('data',$data);       return $this->fetch();    }}