<?phpnamespace app\shop\controller;use think\Db;use think\Request;use think\Session;use think\Url;use app\shop\model\StoreRecord as ThisModel;use app\shop\model\StoreMember;use app\shop\model\StoreProject;class MoneyPt extends Base{    /**	 * [index description]列表	 * @return [type] [description]	 */	public function index()	{        $where = array();		$data = ThisModel::where(['member_id'=>0,'store_id'=>Session::get('shop_id')])->order('id', 'desc')->select();        return $this->fetch('index', [            'list'    => $data,        ]);	}	/**     * [create description]添加方法     * @return [type] [description]     */	public function create()	{		if (Request::instance()->isPOST())		{			$data = Request::instance()->post();            if(empty($data['content'])){                $this->error('数据错误');            }            $data['store_id'] = Session::get('shop_id');			$result = ThisModel::saveVerify($data);			if (true === $result) {                $this->success('新建成功', 'shop/money_pt/index');            } else {                $this->error($result);            }		}		$param = $this->appendarg();		return $this->fetch('create', $param);	}    /**     * [update description]更新方法     * @param  [type] $id [description]主键id     * @return [type]     [description]     */	public function update($id)	{		return $this->fetch('create');	}    /**     * 添加修改时候需要传递参数的话，用此方法，只写一遍     */	public function appendarg(){	    //$prov_data = db::name('cn_prov')->field('code,name')->select();        $infoclass = [                ['id'=>0,'title'=>'普通用户'],                ['id'=>1,'title'=>'银卡'],                ['id'=>2,'title'=>'金卡'],                ['id'=>3,'title'=>'钻石卡'],                ];        $project =  new StoreProject ;        $project_list = $project->select();        return [            'infoclass' => $infoclass,            'project_list' => $project_list,        ];	}    /**     * [delete description]删除方法 多选和单选删除     * @return [type] [description]     */	public function delete(){		if (Request::instance()->isPOST())		{			$id = Request::instance()->post('id/a'); // (/a)方法 将收到的id转为数组			$delmodel = ThisModel::destroy($id);			if($delmodel){			    $this->success('删除成功', 'shop/money_pt/index');			}			else{				$this->error($delmodel->getError());			}	    }	    else{	    	$this->error('请求方式出错!');	    }	}    /**     * [renewfield description]列表更新字段     * @return [type] [description]     */	public function renewfield(){		if (Request::instance()->isPOST())		{            $data = Request::instance()->post();			$validate = validate('User');			$post = Request::instance()->except(['id'],'post');			$post = array_keys($post);            $validate->scene('edit', $post);			if(!$validate->scene('edit')->check($data)){			    $this->error($validate->getError());			}	        $this_model = new ThisModel();	        if($this_model->update($data))            {			    $this->success('更新成功', 'shop/project/index');			}			else{				$this->error($this_model->getError());			}		}	    else{	    	$this->error('请求方式出错!');	    }	}}