ei_pay/index'); ?>?order_sn=" + data.order_sn+"&attach=2";                                //console.log(data.data);                            }, 2000);                        //}                        /*                        if (data.par_type == 2) {                            setTimeout(function () {                                window.location.href = "<?php echo url('home/Alipay/pay'); ?>?order_sn=" + data.order_sn + "&type=2";                            }, 2000);                        }                        if (data.par_type == 3) {                            setTimeout(function () {                                var ordersnum = data.data;                                window.location.href = "<?php echo url('home/wei_pay/index'); ?>?order_sn=" + data.order_sn;                            }, 2000);                        }                        *///                    layer.msg('提交成功!', {//                        time: 2000 //2秒关闭（如果不配置，默认是3秒）//                    }, function(){//                        if(pay_type == 1) {//                            window.location.href="<?php echo url('home/wei_pay/index'); ?>?order_sn="+data.order_sn;//                        }//                    });                    } else {                        layer.msg(data.message);                    }            }        })        }else{            layer.msg('网络错误，请刷新后重试');        }     }     $('#ddzt').click(function(){         $('#ddzt_div').show();         $('#kddf').removeClass('tding_xuan_z');         $('#pay_type').val('ddzt');         $(this).attr('class','tding_xuan_z')     })     $('#kddf').click(function(){         $('#ddzt_div').hide();         $('#ddzt').removeClass('tding_xuan_z');         $('#pay_type').val('kddf');         $(this).attr('class','tding_xuan_z')     })</script>