ction(){            str+=($(this).val())+',';        });        str = str.substr(0, str.length - 1);        // 合并微信支付        $("#input1").click(function(){            $.ajax({                url: "<?php echo url('orders/ordersMerge'); ?>",                type: 'post',                dataType: 'json',                data:{order_sn:str,type:2},                success: function (data) {                    if(data.code==200){                        layer.msg('合并成功!', {                            time: 2000 //2秒关闭（如果不配置，默认是3秒）                        }, function(){                            setTimeout(function () {                                window.location.href =                                        "<?php echo url('home/Wxpay/index'); ?>?order_sn=" + data.order_sn;                            }, 1000);                        });                    }else{                        layer.msg(data.message);                    }                }            });        });        // $("#input2").click(function(){        //     $.ajax({        //         url: "<?php echo url('orders/ordersMerge'); ?>",        //         type: 'post',        //         dataType: 'json',        //         data:{order_sn:str,type:2},        //         success: function (data) {        //             if(data.code==200){        //                 layer.msg('合并成功!', {        //                     time: 2000 //2秒关闭（如果不配置，默认是3秒）        //                 }, function(){        //                     setTimeout(function () {        //                         window.location.href = "<?php echo url('home/Alipay/pay'); ?>?order_sn=" + data.order_sn +        //                                 "&type="+1;        //                     }, 2000);        //                 });        //             }else{        //                 layer.msg(data.message);        //             }        //         }        //     });        // })        //合并线下支付        $("#in