hp echo $vv; ?>')"><span><?php echo $vv; ?></span>                        <?php endif; endforeach; endif; else: echo "" ;endif; ?>                    <div class="clear"></div>                </div>                <?php endforeach; endif; else: echo "" ;endif; ?>                <p>数量</p>                <div class="gg_shu">                    <input name="" type="button" onclick="numreduce()"  value="-">                    <input name="" type="text" id="buy_num" disabled value="1" class="gg_bian">                    <input name="" type="button" onclick="numadd()" value="+">                </div>                <input id="now_price" value=""/>    </div></div><div class="danxx_ti">商品详情</div><div class="danxx">    <?php echo $data['content']; ?></div>    <div class="pb100"></div>    <!--底部nav开始-->    <div class="nfooter">        <ul>            <li><a href="<?php echo url('index/index'); ?>"><img src="__HOME__/images/home.png"><p>首页</p></a></li>            <li><a href="tel:<?php echo $web_hotline; ?>"><img src="__HOME__/images/kefu.png"><p>客服</p></a></li>            <li><a href="javascript:void(0);" onclick="add_car(<?php echo $data['id']; ?>)">加入购物车</a></li>            <li><a href="javascript:void(0);" onclick="add_now(<?php echo $data['id']; ?>)">立即购买</a></li>        </ul>        <div class="clear"></div>    </div>    <!--底部nav结束--></div></body></html><script>    var swiper = new Swiper('.swiper-container', {        autoplay:true,        pagination: {            el: '.swiper-pagination'        },        loop:true,        navigation: {            nextEl: '.swiper-button-next',            prevEl: '.swiper-button-prev',        },    });    $(function() {        $(".gg_xz input").click(function () {            $(this).next("span").addClass("cans_style").siblings().removeClass("cans_style");        })    })    $(".xuan_gg").click(function(){        $(".gg_showbg").show();        sl = $('#xzsl').text()        if(sl==''){            $('#xzsl').text(1)        }        $("html").css({overflow:"hidden"})    })    $(".xuan_guan").click(function(){        $(".gg_showbg").hide();        //计算选中的金额        choose(<?php echo $data['id']; ?>)        $("html").css({overflow:"scroll"})    })    function xzgg(tit){        $('#xzgg').text(tit)    }    function xzsl(tit){        $('#xzsl').text(tit)    }   /*选择属性数量 变化金额*/    function choose(id){        var str='';           $(".gg_xz").find("span").each(function(){               if( $(this).hasClass("cans_style")){                   str+=$(this).text()+'_';               }           })        if (str.length > 0) {            str = str.substr(0, str.length - 1);        }        if(str == ''){               return false;        }        var buy_num = $("#buy_num").val();        $.ajax({            url: "<?php echo url('goods/chooseAttrs'); ?>",            type: 'post',            dataType: 'json',            data:{str:str,id:id,buy_num:buy_num},            success: function (data) {                if(data.code==200){                     $("#price").text(data.price);                     $("#now_price").val(data.price);                     $("#kucun").text(data.kucun);                }else{                    layer.msg('选择参数失败，请重新选择!', {                        time: 2000 //2秒关闭（如果不配置，默认是3秒）                    }, function(){                        //window.location.href="<?php echo url('address/index'); ?>";                        window.location.reload()                    });                }            }        });    }    /*加入购物车*/    var chickable_car=0;//防止表单重复提交    function add_car(goods_id){        var str='';        $(".gg_xz").find("span").each(function(){            if( $(this).hasClass("cans_style")){                str+=$(this).text()+'_';            }        })        if (str.length > 0) {            str = str.substr(0, str.length - 1);        }        var buy_num = parseInt($("#buy_num").val());        if(buy_num == "" || buy_num<1){            tips('请选择数量');            return false;        }        kucun = parseInt($("#kucun").text());        if(kucun < buy_num){            tips('选择数量不能大于库存数量');            return false;        }        var price = $("#now_price").val();        if(price == ""){            tips('请选择规格');            return false;        }       // var yunfei =  $("#yunfei").val();        if(chickable_car==0){            chickable_car=1;        $.ajax({            url: "<?php echo url('car/add'); ?>",            type: 'post',            dataType: 'json',            data:{attr:str,goods_id:goods_id,num:buy_num,price:price},            success: function (data) {                if(data.code==200){                    layer.msg('提交成功!', {                        time: 2000 //2秒关闭（如果不配置，默认是3秒）                    }, function(){                        //window.location.href="<?php echo url('address/index'); ?>";                        window.location.reload()                    });                }else{                }            }        });        }    }    /*加入购物车选择数量++*/    function numadd(){         var buy_num = $("#buy_num").val();         var buy_nums = ++buy_num;         $("#buy_num").val(buy_nums);         $("#xzsl").text(buy_nums)    }    /*加入购物车选择数量—*/    function numreduce(){        var buy_num = $("#buy_num").val();        if (buy_num>1){            var buy_nums = --buy_num;            $("#buy_num").val(buy_nums);            $("#xzsl").text(buy_nums)        }    }    /*立即购买*/    chickable_now=0;    function add_now(goods_id){        var str='';        /*去属性*/        $(".gg_xz").find("span").each(function(){            if( $(this).hasClass("cans_style")){                str+=$(this).text()+'_';            }        })        if (str.length > 0) {            str = str.substr(0, str.length - 1);        }        var buy_num = parseInt($("#buy_num").val());        if(buy_num == "" || buy_num<1){            tips('请选择数量');            return false;        }        kucun = parseInt($("#kucun").text());        if(kucun < buy_num){            tips('选择数量不能大于库存数量');            return false;        }        var price = $("#now_price").val();        if(price == ""){            tips('请选择规格');            return false;        }        var yunfei = 0;        if(chickable_now==0){            chickable_now=1;            window.location.href="<?php echo url('goods/create_order'); ?>?attr="+str+"&goods_id="+goods_id+"&num="+buy_num+"&freight="+yunfei+"&price="+price;        }    }    function sc(id,type){        $.ajax({            url: "<?php echo url('collection/add'); ?>",            type: 'post',            dataType: 'json',            data:{collection_id:id,type:type},            success: function (data) {                if(data.code==200){                    layer.msg('收藏成功!', {                        time: 2000 //2秒关闭（如果不配置，默认是3秒）                    }, function(){                        window.location.reload()                    });                }else{                    layer.msg(data.message);                }            }        });    }    function qx(collection_id){        $.ajax({            url: "<?php echo url('collection/del'); ?>",            type: 'post',            dataType: 'json',            data:{collection_id:collection_id},            success: function (data) {                if(data.code==200){                    layer.msg('取消成功!', {                        time: 2000 //2秒关闭（如果不配置，默认是3秒）                    }, function(){                        window.location.reload()                    });                }else{                    layer.msg(data.message);                }            }        });    }</script><script type="text/javascript" src="http://res.wx.qq.com/open/js/jweixin-1.0.0.js"></script><script type="text/javascript">    function fff() {        $(".layer1").hide();    }    function share_btn(){        $(".layer1").show();    }    var url = "<?php echo $url; ?>";    wx.config({        debug: false, // 开启调试模式,调用的所有api的返回值会在客户端alert出来，若要查看传入的参数，可以在pc端打开，参数信息会通过log打出，仅在pc端时才会打印。        //<?php echo $share['appId']; ?>        appId: '', // 必填，公众号的唯一标识        //<?php echo $share['timestamp']; ?>        timestamp:"" , // 必填，生成签名的时间戳        //<?php echo $share['nonceStr']; ?>        nonceStr: '', // 必填，生成签名的随机串        //<?php echo $share['signature']; ?>        signature: '',// 必填，签名，见附录1        jsApiList: [            // 所有要调用的 API 都要加到这个列表中            'onMenuShareAppMessage',            'onMenuShareTimeline',        ] // 必填，需要使用的JS接口列表，所有JS接口列表见附录2    });    wx.ready(function(){        wx.onMenuShareAppMessage({            title: '',            desc: '<?php echo $data['title']; ?>',            link: url,            type: 'link', // 分享类型,music、video或link，不填默认为link            imgUrl: '<?php echo $data['picurl']; ?>', // 分享图标            dataUrl: '', // 如果type是music或video，则要提供数据链接，默认为空            success: function () {                layer.msg("分享成功");                location.reload();            },            cancel: function () {                layer.msg("取消成功");            }        });        wx.onMenuShareTimeline({            title: '',            desc: '<?php echo $data['title']; ?>',            link: url,            type: 'link', // 分享类型,music、video或link，不填默认为link            imgUrl: '<?php echo $data['picurl']; ?>', // 分享图标            dataUrl: '', // 如果type是music或video，则要提供数据链接，默认为空            success: function () {                layer.msg("分享成功");                location.reload();            },            cancel: function () {                layer.msg("取消成功");            }        });    });</script>