     $('#status').val(1);            }else{                $('#status').val(0);            }        });        var flag = true;        function add_address() {            if(flag) {                var status = $('#status').val();                var username = $('#username').val();                if(username == "") {                    layer.msg('收货人不能为空');                    $('#username').focus();                    return false;                }                if(username.length < 2) {                    layer.msg('收货人不能小于2字符');                    $('#username').focus();                    return false;                }                if(username.length > 10) {                    layer.msg('收货人请小于10字符');                    $('#username').focus();                    return false;                }                var mobile = $('#mobile').val();                if (mobile == "") {                    layer.msg('手机号不能为空');                    $('#mobile').focus();                    return false;                }                shous = /^1[3456789]\d{9}$/;                if (shous.test(mobile) === false) {                    layer.msg('手机号格式错误');                    $('#mobile').focus();                    return false;                }                var prov = $('#prov').val();                var city = $('#city').val();                var area = $('#area').val();                if(prov == "" || city == '' || area=='') {                    layer.msg('请选择省市区');                    return false;                }                var address = $('#address').val();                if(address == "") {                    layer.msg('请填写详细地址');                    $('#address').focus();                    return false;                }                if(address.length < 3) {                    layer.msg('详细地址不能小于3字符');                    $('#address').focus();                    return false;                }                if(address.length > 40) {                    layer.msg('详细地址不能大于40字符');                    $('#address').focus();                    return false;                }                //楼号                tower_num = $('#tower_num').val();                flag = false;                $.post("<?php echo url('home/address/add_address'); ?>",{username:username,mobile:mobile,prov:prov,city:city,area:area,address:address,status:status,tower_num:tower_num},function(data){                    if(data ==1 ) {                        layer.msg('添加成功',{                            time : 1000                        },function(){                            window.location.href="<?php echo url('home/address/index'); ?>";                        });                        return false;                    } else {                        layer.msg('网络异常重新尝试');                        return false;                    }                });            } else {                return false;            }        }    </script>